import React, {PureComponent} from "react";
import {
    StyleSheet, 
    View, 
} from  "react-native";
import { IconButton, Colors, withTheme, Title, Headline } from "react-native-paper";

class RecordAudio extends PureComponent {
    state = { isRecording: false };

    startRecording = () => {
         this.setState({isRecording: true});
    }

    stopRecording = () => {
        this.setState({isRecording: false});
    }

    render() {
        const {isRecording} = this.state;
        const {theme} = this.props;
        return (
            <View style={styles.container}>
                <View style={{paddingTop: 32}}>
                    <Title style={{fontSize: 40}}>00:00</Title>
                </View>
                <View style={{display: "flex", alignItems: "center", flexDirection:"row"}}>
                    <IconButton
                        icon="harddisk"
                        size={42}
                    />
                    <IconButton
                        icon={isRecording ? "stop-circle" : "microphone"}
                        size={80}
                        color={theme.colors.primary}
                        onPress={isRecording ? this.stopRecording : this.startRecording}
                        animated
                    />
                    <IconButton
                        icon="check"
                        size={42}
                        disabled
                    />
                </View>
            </View>
        );
    }
}

export default withTheme(RecordAudio);

const styles = StyleSheet.create({
    container: { 
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        backgroundColor: "#121212",
        paddingTop: 16,
        paddingBottom: 16,
        flex: 1,
    },
});
