import React, {PureComponent} from "react";
import {
    StyleSheet, 
    View, 
} from  "react-native";
import { IconButton, withTheme, Text } from "react-native-paper";

function msToString (s) {
    let ms = String(s % 1000).slice(0, 2);
    let secs = String(Math.floor((s - ms) / 1000));
    if (secs.length == 1) {
        secs = "0" + secs;
    }
    if (ms.length == 1) {
        ms = "0" + ms;
    }

    return secs + ':' + ms;
  }

class RecordAudio extends PureComponent {
    state = { isRecording: false, timeElapsed: "00:00", micBars: 0 };

    startRecording = () => {
         this.setState({isRecording: true}, this.startCounter);
    }

    startCounter = () => {
        this.startTime = Date.now();
        this.tick();
    }

    tick = () => {
        const {isRecording} = this.state;
        if(isRecording) {
            const msElapsed = Date.now() - this.startTime;
            this.setState({timeElapsed: msToString(msElapsed)});
            setTimeout(this.tick, 25);
        }
    }

    stopRecording = () => {
        this.setState({isRecording: false});
    }

    displayMicBars = () => {
        const {micBars} = this.state;
        const {theme} = this.props;
        const bars = [];
        for (let i = 1; i < 11; i++) {
          bars.push(
              <View
                style={{
                  height: 80,
                  width: "6%",
                  marginRight: 8,
                  backgroundColor: micBars >= i ? theme.colors.primary : theme.colors.surface,
                }}
              ></View>,
          );
        }
        return bars;
      };

    render() {
        const {isRecording, timeElapsed} = this.state;
        const {theme} = this.props;
        return (
            <View style={styles.container}>
                <View style={{marginTop: 32, display: "none"}}>
                    <Text style={{fontSize: 50}}>{timeElapsed}</Text>
                </View>
                <View style={{display: "flex", flexDirection: "row"}}>
                    {this.displayMicBars()}
                </View>
                <View style={{display: "flex", alignItems: "center", flexDirection:"row"}}>
                    <IconButton
                        icon="harddisk"
                        size={42}
                    />
                    <IconButton
                        icon={isRecording ? "stop-circle" : "microphone"}
                        size={80}
                        color={theme.colors.primary}
                        onPress={isRecording ? this.stopRecording : this.startRecording}
                        animated
                    />
                    <IconButton
                        icon="check"
                        size={42}
                        disabled
                    />
                </View>
            </View>
        );
    }
}

export default withTheme(RecordAudio);

const styles = StyleSheet.create({
    container: { 
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        backgroundColor: "#121212",
        paddingTop: 16,
        paddingBottom: 16,
        flex: 1,
    },
});
