import React, { PureComponent } from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View, Dimensions, ScrollView } from 'react-native';
import { 
  Provider as PaperProvider,
  DarkTheme,
  Appbar,
  Text,   
  Surface
} from 'react-native-paper';
import { NativeRouter } from 'react-router-native';
import { Switch } from 'react-native-gesture-handler';
import SelectVoice from "./pages/SelectVoice";

const theme = {
  ...DarkTheme,
  colors: {
    ...DarkTheme.colors,
    surface: "rgb(40, 40, 40)"
  },
  adaptive: true,
}

const voices = [
  {
    name: "Donald Trump",
    image: require('./assets/trump_512x512.png'),
  },
  {
    name: "Kim Kardashian",
    image: require('./assets/kim_kardashian_512x512.png'),
  },
  {
    name: "Kanye West",
    image: require('./assets/kanye_west_512x512.png'),
  },
  {
    name: "Justin Bieber",
    image: require('./assets/justin_bieber_512x512.png'),
  },
  {
    name: "Billie Eilish",
    image: require('./assets/billie_eilish_512x512.png'),
  }
];

export default class App extends PureComponent {
  state = {
    page: "selectVoice"
  }
  render() {
    return (
      <NativeRouter>
        <PaperProvider theme={theme}>
          <Appbar.Header style={{elevation: 4}}>
            <Appbar.Content title="Select a Voice"/>
            <Appbar.Action icon="lock-open-outline" onPress={() => {}} />
          </Appbar.Header>
          <Switch>
            <Route path="/" render={() => <SelectVoice voices={voices}/>}>
            </Route>
          </Switch>
          <StatusBar style="auto" />
        </PaperProvider>
      </NativeRouter> 
    );
  }
}
